// Twitter Status Callback
function twitterCallback2(e){var t=[];for(var n=0;n<e.length;n++){var r=e[n].user.profile_image_url,i=e[n].user.screen_name,s=e[n].text.replace(/((https?|s?ftp|ssh)\:\/\/[^"\s\<\>]*[^.,;'">\:\s\<\>\)\]\!])/g,function(e){return'<a href="'+e+'">'+e+"</a>"}).replace(/\B@([_a-z0-9]+)/ig,function(e){return e.charAt(0)+'<a href="http://twitter.com/'+e.substring(1)+'">'+e.substring(1)+"</a>"});t.push('<li class="twit"><p class="twit_avatar"><a href="http://twitter.com/'+i+"/statuses/"+e[n].id_str+'"><img src="'+r+'" alt="">'+"@"+i+'</a></p> <p class="twit_tweet">'+s+'</p> <p><small><time class="twit_timestamp" pubdate><a href="http://twitter.com/'+i+"/statuses/"+e[n].id_str+'">'+relative_time(e[n].created_at)+"</a></small></p></li>")}document.getElementById("twitter_update_list").innerHTML=t.join("")}function relative_time(e){var t=e.split(" "),n=new Date,r=new Array("Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"),i=n.getMonth(),s="";e=t[1]+" "+t[2]+" "+t[5]+" "+t[3];n.setTime(Date.parse(e));s=r[i];s+=" "+n.getDate();s+=",";s+=" "+n.getFullYear();return s};